ソースコード 3: repdec.c <pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#define MAX 100

void repdec(unsigned int n, unsigned int d, unsigned int base)
{
    unsigned int i, k;
    unsigned int a[MAX + 1], p[MAX];

    for (i = 0; i &lt; MAX; i++)
        p[i] = 0;

    a[0] = n / d;
    n = n % d;
    k = 0;

    while (1) {
        p[n] = ++k;
        n = n * base;
        a[k] = n / d;
        n = n % d;
        if (p[n] != 0)
            break;
    }

    printf("%u.", a[0]);
    for (i = 1; i &lt; p[n]; i++)
        printf("%u", a[i]);
    if (p[n] &lt; k || a[k] != 0) {
        printf("{");
        for (i = p[n]; i &lt;= k; i++)
            printf("%u", a[i]);
        printf("}");
    }
    printf("\n");

    printf("p = [");
    for (i = 0; i &lt; d; i++)
        printf("%u ", p[i]);
    printf("]\n");
}

int main (void)
{
    repdec(1, 3, 10);
    return 0;
}
</code></pre>

