struct vertex *create(int x) {
    struct vertex *p;
    p = (struct vertex *) malloc(sizeof(struct vertex));
    p-&gt;value = x;
    p-&gt;left = NULL;
    p-&gt;right = NULL;
    return p;
}

void insert (int x) {
    struct vertex *p;

    if (tree == NULL) {
        tree = create(x);
        return;
    }

    p = tree;
    do {
        if (p-&gt;value == x) break;
        else if (x &lt; p-&gt;value &amp;&amp; p-&gt;left == NULL ) {
            p-&gt;left = create(x);
            break;
        } else if (p-&gt;value &lt; x &amp;&amp; p-&gt;right == NULL) {
            p-&gt;right = create(x);
            break;
        }
        if (x &lt; p-&gt;value)
            p = p-&gt;left;
        else
            p = p-&gt;right;
    } while (1);
    return;
}

void eliminate(int x) {
    struct vertex *f, *p, *q;

    p = tree;
    if (p == NULL) return;
    do {
        f = p;
        if (x &lt; p-&gt;value)
            p = p-&gt;left;
        else if (p-&gt;value &lt; x)
            p = p-&gt;right;
    } while (x != p-&gt;value);

    if (p-&gt;left == NULL || p-&gt;right == NULL) {
        if (p-&gt;right == NULL)
            q = p-&gt;left;
        else
            q = p-&gt;right;
        if (p == tree)
            tree == q;
        else {
            if (f-&gt;left == p)
                f-&gt;left = q;
            else
                f-&gt;right = q;
        }
    } else {
        q = p-&gt;right;
        f = q;
        while (q-&gt;left != NULL) {
            f = q;
            q = q-&gt;left;
        }
        p-&gt;value = q-&gt;value;
        if (q == f)
            p-&gt;right = q-&gt;right;
        else
            f-&gt;left = q-&gt;right;
    }
    return;
}

